import React, { useState, useEffect } from 'react';
import { useCampaign } from '../contexts/CampaignContext';
import './CombatLoot.css';

const CombatLoot = () => {
  const [entries, setEntries] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const { selectedCampaign } = useCampaign();
  const [formData, setFormData] = useState({
    name: '',
    loot_description: '',
    gold_pieces: '',
    silver_pieces: '',
    copper_pieces: ''
  });

  useEffect(() => {
    if (selectedCampaign) {
      fetchLootEntries();
    }
  }, [selectedCampaign]);

  const fetchLootEntries = async () => {
    try {
      const response = await fetch(`http://localhost:3001/api/campaigns/${selectedCampaign.id}/loot`);
      if (!response.ok) throw new Error('Failed to fetch loot entries');
      const data = await response.json();
      setEntries(data);
      setIsLoading(false);
    } catch (error) {
      console.error('Error fetching loot entries:', error);
      setIsLoading(false);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: name.includes('pieces') ? (value === '' ? '' : parseInt(value) || 0) : value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!selectedCampaign) {
      alert('Please select a campaign first');
      return;
    }

    try {
      const response = await fetch(`http://localhost:3001/api/campaigns/${selectedCampaign.id}/loot`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          ...formData,
          gold_pieces: parseInt(formData.gold_pieces) || 0,
          silver_pieces: parseInt(formData.silver_pieces) || 0,
          copper_pieces: parseInt(formData.copper_pieces) || 0,
          campaign_id: selectedCampaign.id,
          timestamp: new Date().toISOString()
        }),
      });

      if (!response.ok) throw new Error('Failed to create loot entry');

      // Clear form and refresh entries
      setFormData({
        name: '',
        loot_description: '',
        gold_pieces: '',
        silver_pieces: '',
        copper_pieces: ''
      });
      await fetchLootEntries();
    } catch (error) {
      console.error('Error creating loot entry:', error);
      alert('Failed to create loot entry: ' + error.message);
    }
  };

  if (isLoading) {
    return <div className="loading">Loading loot entries...</div>;
  }

  return (
    <div className="combat-loot-container">
      <div className="loot-form-section">
        <h2>Add New Loot Entry</h2>
        <form onSubmit={handleSubmit} className="loot-form">
          <div className="form-group">
            <label htmlFor="name">Combat Name</label>
            <input
              type="text"
              id="name"
              name="name"
              value={formData.name}
              onChange={handleInputChange}
              required
              placeholder="Enter combat name"
            />
          </div>

          <div className="form-group">
            <label htmlFor="loot_description">Loot Description</label>
            <textarea
              id="loot_description"
              name="loot_description"
              value={formData.loot_description}
              onChange={handleInputChange}
              rows="4"
              placeholder="Describe the loot gained..."
            />
          </div>

          <div className="currency-inputs">
            <div className="form-group">
              <label htmlFor="gold_pieces">Gold Pieces</label>
              <input
                type="number"
                id="gold_pieces"
                name="gold_pieces"
                value={formData.gold_pieces}
                onChange={handleInputChange}
                min="0"
                placeholder="0"
              />
            </div>

            <div className="form-group">
              <label htmlFor="silver_pieces">Silver Pieces</label>
              <input
                type="number"
                id="silver_pieces"
                name="silver_pieces"
                value={formData.silver_pieces}
                onChange={handleInputChange}
                min="0"
                placeholder="0"
              />
            </div>

            <div className="form-group">
              <label htmlFor="copper_pieces">Copper Pieces</label>
              <input
                type="number"
                id="copper_pieces"
                name="copper_pieces"
                value={formData.copper_pieces}
                onChange={handleInputChange}
                min="0"
                placeholder="0"
              />
            </div>
          </div>

          <button type="submit" className="submit-button">Add Loot Entry</button>
        </form>
      </div>

      <div className="loot-entries-section">
        <h2>Loot History</h2>
        <div className="loot-entries">
          {entries.length === 0 ? (
            <p className="no-entries">No loot entries yet</p>
          ) : (
            entries.map(entry => (
              <div key={entry.id} className="loot-entry">
                <div className="entry-header">
                  <h3>{entry.name}</h3>
                  <span className="timestamp">
                    {new Date(entry.timestamp).toLocaleString()}
                  </span>
                </div>
                <p className="loot-description">{entry.loot_description}</p>
                <div className="currency-summary">
                  <span className="gold">{entry.gold_pieces} GP</span>
                  <span className="silver">{entry.silver_pieces} SP</span>
                  <span className="copper">{entry.copper_pieces} CP</span>
                </div>
              </div>
            ))
          )}
        </div>
      </div>
    </div>
  );
};

export default CombatLoot;
