import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';
import './Maps.css';
import { useCampaign } from '../contexts/CampaignContext';
import IconButton from '@mui/material/IconButton';
import Menu from '@mui/material/Menu';
import MenuItem from '@mui/material/MenuItem';
import { DotsThreeVertical } from "@phosphor-icons/react";

// Marker configuration
const MARKER_COLORS = {
  '#FF0000': 'Red',
  '#00FF00': 'Green',
  '#0000FF': 'Blue',
  '#FFFF00': 'Yellow',
  '#FF00FF': 'Magenta',
  '#00FFFF': 'Cyan',
  '#000000': 'Black'
};

const Maps = ({ onMapChange }) => {
  const { id: mapId } = useParams();
  const navigate = useNavigate();
  const [map, setMap] = useState(null);
  const [leafletMap, setLeafletMap] = useState(null);
  const [mapData, setMapData] = useState(null);
  const [markers, setMarkers] = useState([]);
  const [menuAnchorEl, setMenuAnchorEl] = useState(null);
  const [imageDimensions, setImageDimensions] = useState(null);
  const { selectedCampaign } = useCampaign();
  const mapContainerRef = React.useRef(null);

  // Fetch map data
  useEffect(() => {
    if (selectedCampaign && mapId) {
      fetchMap();
      fetchMarkers();
    }
  }, [mapId, selectedCampaign]);

  const fetchMap = async () => {
    if (!selectedCampaign) return;

    try {
      const response = await fetch(`http://localhost:3001/api/maps/${mapId}?campaign_id=${selectedCampaign.id}`);
      if (!response.ok) throw new Error('Failed to fetch map');
      const data = await response.json();
      setMapData(data);

      // Load image dimensions
      const img = new Image();
      img.onload = () => {
        setImageDimensions({ width: img.width, height: img.height });
        initializeMap(img.width, img.height, data);
      };
      img.src = `http://localhost:3001/maps/${data.path}`;

      if (onMapChange) onMapChange(data);
    } catch (error) {
      console.error('Error fetching map:', error);
      navigate('/maps');
    }
  };

  const fetchMarkers = async () => {
    if (!selectedCampaign || !mapId) return;

    try {
      const response = await fetch(`http://localhost:3001/api/maps/${mapId}/markers?campaign_id=${selectedCampaign.id}`);
      if (!response.ok) throw new Error('Failed to fetch markers');
      const data = await response.json();
      setMarkers(data);

      // If map is already initialized, add markers
      if (leafletMap) {
        addMarkersToMap(data, leafletMap);
      }
    } catch (error) {
      console.error('Error fetching markers:', error);
    }
  };

  const createMarkerIcon = (color = '#FF0000') => {
    return L.divIcon({
      className: 'custom-marker',
      html: `<div class="marker-icon" style="color: ${color}">
        <svg xmlns="http://www.w3.org/2000/svg" width="32" height="48" viewBox="0 0 256 256">
          <path fill="currentColor" d="M128,16a88.1,88.1,0,0,0-88,88c0,75.3,80,132.17,83.41,134.55a8,8,0,0,0,9.18,0C136,236.17,216,179.3,216,104A88.1,88.1,0,0,0,128,16Z" stroke="rgba(0, 0, 0, 0.25)" stroke-width="16"/>
        </svg>
      </div>`,
      iconSize: [32, 48],
      iconAnchor: [16, 48],
      popupAnchor: [0, -24]
    });
  };

  const createPopupContent = (markerData, isNewMarker = false) => {
    // Create popup content as a string instead of DOM elements
    return `
      <div class="marker-content">
        <form class="marker-form" onsubmit="return false;">
          <input 
            type="text" 
            name="label" 
            value="${markerData.label || ''}" 
            placeholder="Marker Label" 
            class="marker-input" 
            required
          />
          <textarea 
            name="description" 
            placeholder="Description (optional)" 
            class="marker-input" 
            rows="2"
          >${markerData.description || ''}</textarea>
          <select name="color" class="marker-input">
            ${Object.entries(MARKER_COLORS).map(([value, label]) => `
              <option value="${value}" ${value === (markerData.color || '#FF0000') ? 'selected' : ''}>
                ${label}
              </option>
            `).join('')}
          </select>
          <div class="marker-actions">
            <button type="button" class="delete-button" data-action="${isNewMarker ? 'cancel' : 'delete'}">
              ${isNewMarker ? 'Cancel' : 'Delete'}
            </button>
            <button type="button" class="dnd-button" data-action="${isNewMarker ? 'create' : 'update'}">
              ${isNewMarker ? 'Add Marker' : 'Save Changes'}
            </button>
          </div>
        </form>
      </div>
    `;
  };

  const addMarkersToMap = (markersData, targetMap) => {
    if (!targetMap) return;

    // Only clear temporary markers
    targetMap.eachLayer((layer) => {
      if (layer instanceof L.Marker && !layer.options.permanent) {
        targetMap.removeLayer(layer);
      }
    });

    // Add new markers
    markersData.forEach(markerData => {
      if (!markerData || typeof markerData.lat !== 'number' || typeof markerData.lng !== 'number') {
        console.warn('Invalid marker data:', markerData);
        return;
      }

      // Check if marker already exists
      let existingMarker = null;
      targetMap.eachLayer((layer) => {
        if (layer instanceof L.Marker && layer.options.markerId === markerData.id) {
          existingMarker = layer;
        }
      });

      // If marker exists, update it
      if (existingMarker) {
        existingMarker.setIcon(createMarkerIcon(markerData.color || '#FF0000'));
        existingMarker.setPopupContent(createPopupContent(markerData, false));
        return;
      }

      // Create new marker
      const marker = L.marker([markerData.lat, markerData.lng], {
        icon: createMarkerIcon(markerData.color || '#FF0000'),
        permanent: true,
        markerId: markerData.id
      });

      const popup = L.popup({
        className: 'custom-popup',
        closeButton: true,
        autoClose: false,
        closeOnClick: false
      });

      popup.setContent(createPopupContent(markerData, false));
      marker.bindPopup(popup);

      // Add event listeners to the marker
      marker.on('popupopen', (e) => {
        const popup = e.popup;
        const container = popup.getElement();
        if (!container) return;

        // Get form elements
        const form = container.querySelector('form');
        const deleteBtn = container.querySelector('[data-action="delete"]');
        const updateBtn = container.querySelector('[data-action="update"]');

        // Handle delete
        if (deleteBtn) {
          deleteBtn.onclick = async () => {
            try {
              const response = await fetch(`http://localhost:3001/api/maps/${mapId}/markers/${markerData.id}?campaign_id=${selectedCampaign.id}`, {
                method: 'DELETE',
              });

              if (!response.ok) throw new Error('Failed to delete marker');

              setMarkers(prev => prev.filter(m => m.id !== markerData.id));
              targetMap.removeLayer(marker);
            } catch (error) {
              console.error('Error deleting marker:', error);
            }
          };
        }

        // Handle update
        if (updateBtn) {
          updateBtn.onclick = async () => {
            const formData = new FormData(form);
            const updatedMarker = {
              ...markerData,
              label: formData.get('label'),
              description: formData.get('description'),
              color: formData.get('color')
            };

            try {
              const response = await fetch(`http://localhost:3001/api/maps/${mapId}/markers/${markerData.id}`, {
                method: 'PUT',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({
                  ...updatedMarker,
                  campaign_id: selectedCampaign.id
                }),
              });

              if (!response.ok) throw new Error('Failed to update marker');
              const savedMarker = await response.json();

              setMarkers(prev => prev.map(m => m.id === savedMarker.id ? savedMarker : m));
              marker.setIcon(createMarkerIcon(savedMarker.color));
              marker.closePopup();
            } catch (error) {
              console.error('Error updating marker:', error);
            }
          };
        }
      });

      marker.addTo(targetMap);
    });
  };

  const initializeMap = (width, height, mapData) => {
    if (!mapContainerRef.current) return;

    // Clean up existing map
    if (leafletMap) {
      leafletMap.remove();
    }

    const bounds = [[0, 0], [height, width]];
    const map = L.map(mapContainerRef.current, {
      crs: L.CRS.Simple,
      maxZoom: 2,
      minZoom: -2,
      maxBounds: bounds,
      zoomControl: true
    });

    L.imageOverlay(`http://localhost:3001/maps/${mapData.path}`, bounds).addTo(map);
    map.fitBounds(bounds);

    // Set map instance first
    setLeafletMap(map);

    // Add existing markers
    if (markers.length > 0) {
      addMarkersToMap(markers, map);
    }

    // Set up click handler for new markers
    map.on('click', (e) => {
      if (!selectedCampaign || !mapId) return;

      const { lat, lng } = e.latlng;

      // Remove any existing temporary markers
      map.eachLayer((layer) => {
        if (layer instanceof L.Marker && !layer.options.permanent) {
          map.removeLayer(layer);
        }
      });

      const tempMarker = L.marker([lat, lng], {
        icon: createMarkerIcon('#FF0000'),
        permanent: false
      });

      const tempMarkerData = {
        lat,
        lng,
        label: '',
        description: '',
        color: '#FF0000'
      };

      const popup = L.popup({
        className: 'custom-popup',
        closeButton: true,
        autoClose: false,
        closeOnClick: false
      });

      popup.setContent(createPopupContent(tempMarkerData, true));
      tempMarker.bindPopup(popup);

      // Add event listeners when popup opens
      tempMarker.on('popupopen', (e) => {
        const popup = e.popup;
        const container = popup.getElement();
        if (!container) return;

        // Get form elements
        const form = container.querySelector('form');
        const cancelBtn = container.querySelector('[data-action="cancel"]');
        const createBtn = container.querySelector('[data-action="create"]');

        // Handle cancel
        if (cancelBtn) {
          cancelBtn.onclick = () => {
            map.removeLayer(tempMarker);
          };
        }

        // Handle create
        if (createBtn) {
          createBtn.onclick = async () => {
            const formData = new FormData(form);
            const newMarker = {
              ...tempMarkerData,
              label: formData.get('label'),
              description: formData.get('description'),
              color: formData.get('color')
            };

            try {
              const response = await fetch(`http://localhost:3001/api/maps/${mapId}/markers`, {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({
                  ...newMarker,
                  campaign_id: selectedCampaign.id
                }),
              });

              if (!response.ok) throw new Error('Failed to create marker');
              const savedMarker = await response.json();

              setMarkers(prev => [...prev, savedMarker]);
              map.removeLayer(tempMarker);

              // Add just the new marker without clearing others
              const marker = L.marker([savedMarker.lat, savedMarker.lng], {
                icon: createMarkerIcon(savedMarker.color || '#FF0000'),
                permanent: true,
                markerId: savedMarker.id
              });

              const newPopup = L.popup({
                className: 'custom-popup',
                closeButton: true,
                autoClose: false,
                closeOnClick: false
              }).setContent(createPopupContent(savedMarker, false));

              marker.bindPopup(newPopup);
              marker.addTo(map);
            } catch (error) {
              console.error('Error creating marker:', error);
            }
          };
        }
      });

      tempMarker.addTo(map).openPopup();
    });
  };

  const handleMarkerCreate = () => {};
  const handleMarkerUpdate = () => {};
  const handleMarkerDelete = () => {};
  const handleMarkerEdit = () => {};

  if (!mapData || !imageDimensions) {
    return <div>Loading map...</div>;
  }

  return (
    <div className="map-container">
      <div style={{ display: 'flex', alignItems: 'center', padding: '0 16px' }}>
        <h2 style={{ margin: 0, display: 'flex', alignItems: 'center' }}>{mapData.name}</h2>
        <IconButton
          aria-label="more"
          aria-controls="map-menu"
          aria-haspopup="true"
          onClick={(e) => setMenuAnchorEl(e.currentTarget)}
          style={{ marginLeft: '8px', color: '#111827' }}
        >
          <DotsThreeVertical size={24} />
        </IconButton>
      </div>

      <Menu
        id="map-menu"
        anchorEl={menuAnchorEl}
        keepMounted
        open={Boolean(menuAnchorEl)}
        onClose={() => setMenuAnchorEl(null)}
      >
        <MenuItem onClick={() => setMenuAnchorEl(null)}>Rename</MenuItem>
        <MenuItem onClick={() => setMenuAnchorEl(null)}>Delete</MenuItem>
      </Menu>

      <div ref={mapContainerRef} style={{ height: 'calc(100vh - 80px)', width: '100%' }} />
    </div>
  );
};

export default Maps;
