import React from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Sidebar from './components/Sidebar';
import Maps from './components/Maps';
import CombatLoot from './components/CombatLoot';
import Timeline from './components/Timeline';
import Log from './components/Log';
import { CampaignProvider } from './contexts/CampaignContext';
import './App.css';

function App() {
  const [selectedMap, setSelectedMap] = React.useState(null);

  return (
    <Router>
      <CampaignProvider>
        <div className="app">
          <Sidebar onMapChange={setSelectedMap} />
          <main className="main-content">
            <Routes>
              <Route path="/maps/:id" element={<Maps onMapChange={setSelectedMap} />} />
              <Route path="/combat/loot" element={<CombatLoot />} />
              <Route path="/timeline" element={<Timeline />} />
              <Route path="/log" element={<Log />} />
              <Route path="/" element={
                <div className="welcome-screen">
                  <h1>Welcome to Flying Dog Inn VTT</h1>
                  <p>Select a map to get started</p>
                </div>
              } />
            </Routes>
          </main>
        </div>
      </CampaignProvider>
    </Router>
  );
}

export default App;
