import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';
import './Maps.css';
import { useCampaign } from '../contexts/CampaignContext';
import IconButton from '@mui/material/IconButton';
import Menu from '@mui/material/Menu';
import MenuItem from '@mui/material/MenuItem';
import { DotsThreeVertical } from "@phosphor-icons/react";

// Marker configuration
const MARKER_COLORS = {
  '#FF0000': 'Red',
  '#00FF00': 'Green',
  '#0000FF': 'Blue',
  '#FFFF00': 'Yellow',
  '#FF00FF': 'Magenta',
  '#00FFFF': 'Cyan',
  '#000000': 'Black'
};

const Maps = ({ onMapChange }) => {
  const { id: mapId } = useParams();
  const navigate = useNavigate();
  const [mapData, setMapData] = useState(null);
  const [markers, setMarkers] = useState([]);
  const [menuAnchorEl, setMenuAnchorEl] = useState(null);
  const [imageDimensions, setImageDimensions] = useState(null);
  const [isLoading, setIsLoading] = useState(true);
  const { selectedCampaign } = useCampaign();
  const mapContainerRef = React.useRef(null);
  const leafletMapRef = React.useRef(null);

  // Cleanup function
  const cleanupMap = () => {
    if (leafletMapRef.current) {
      leafletMapRef.current.remove();
      leafletMapRef.current = null;
    }
  };

  // Reset state when map changes
  useEffect(() => {
    setIsLoading(true);
    setMapData(null);
    setImageDimensions(null);
    setMarkers([]);
    cleanupMap();
    return cleanupMap;
  }, [mapId]);

  // Cleanup on unmount
  useEffect(() => {
    return cleanupMap;
  }, []);

  // Fetch map data and markers
  useEffect(() => {
    if (selectedCampaign && mapId) {
      fetchMap();
      fetchMarkers();
    }
  }, [mapId, selectedCampaign]);

  const fetchMap = async () => {
    if (!selectedCampaign) return;

    try {
      const response = await fetch(`http://localhost:3001/api/maps/${mapId}?campaign_id=${selectedCampaign.id}`);
      if (!response.ok) throw new Error('Failed to fetch map');
      const data = await response.json();
      setMapData(data);

      // Load image dimensions
      const img = new Image();
      img.onload = () => {
        console.log('Image loaded:', img.width, img.height);
        setImageDimensions({ width: img.width, height: img.height });
        setIsLoading(false);
      };
      img.onerror = (error) => {
        console.error('Failed to load map image:', error);
        setIsLoading(false);
      };
      img.src = `http://localhost:3001/maps/${data.path}`;

      if (onMapChange) onMapChange(data);
    } catch (error) {
      console.error('Error fetching map:', error);
      setIsLoading(false);
      navigate('/maps');
    }
  };

  const fetchMarkers = async () => {
    if (!selectedCampaign || !mapId) return;

    try {
      const response = await fetch(`http://localhost:3001/api/maps/${mapId}/markers?campaign_id=${selectedCampaign.id}`);
      if (!response.ok) throw new Error('Failed to fetch markers');
      const data = await response.json();

      // Convert lat/lng to numbers
      const parsedMarkers = data.map(marker => ({
        ...marker,
        lat: Number(marker.lat),
        lng: Number(marker.lng)
      }));

      setMarkers(parsedMarkers);

      // If map is already initialized, add markers
      if (leafletMapRef.current) {
        addMarkersToMap(parsedMarkers, leafletMapRef.current);
      }
    } catch (error) {
      console.error('Error fetching markers:', error);
    }
  };

  const createMarkerIcon = (color = '#FF0000') => {
    return L.divIcon({
      className: 'custom-marker',
      html: `<div class="marker-icon" style="color: ${color}">
        <svg xmlns="http://www.w3.org/2000/svg" width="32" height="48" viewBox="0 0 256 256">
          <path fill="currentColor" d="M128,16a88.1,88.1,0,0,0-88,88c0,75.3,80,132.17,83.41,134.55a8,8,0,0,0,9.18,0C136,236.17,216,179.3,216,104A88.1,88.1,0,0,0,128,16Z" stroke="rgba(0, 0, 0, 0.25)" stroke-width="16"/>
        </svg>
      </div>`,
      iconSize: [32, 48],
      iconAnchor: [16, 48],
      popupAnchor: [0, -24]
    });
  };

  const createPopupContent = (markerData, isNewMarker = false, handlers) => {
    const uniqueId = Math.random().toString(36).substring(7);
    const formId = `marker-form-${uniqueId}`;
    const deleteId = `delete-btn-${uniqueId}`;
    const submitId = `submit-btn-${uniqueId}`;

    // Create popup content with unique IDs
    const content = `
      <div class="marker-content">
        <form id="${formId}" class="marker-form">
          <input 
            type="text" 
            name="label" 
            value="${markerData.label || ''}" 
            placeholder="Marker Label" 
            class="marker-input" 
            required
          />
          <textarea 
            name="description" 
            placeholder="Description (optional)" 
            class="marker-input" 
            rows="2"
          >${markerData.description || ''}</textarea>
          <select name="color" class="marker-input">
            ${Object.entries(MARKER_COLORS).map(([value, label]) => `
              <option value="${value}" ${value === (markerData.color || '#FF0000') ? 'selected' : ''}>
                ${label}
              </option>
            `).join('')}
          </select>
          <div class="marker-actions">
            <button type="button" id="${deleteId}" class="delete-button">
              ${isNewMarker ? 'Cancel' : 'Delete'}
            </button>
            <button type="button" id="${submitId}" class="dnd-button">
              ${isNewMarker ? 'Add Marker' : 'Save Changes'}
            </button>
          </div>
        </form>
      </div>
    `;

    // Return both content and IDs for event binding
    return { content, formId, deleteId, submitId };
  };

  const bindMarkerEvents = (marker, markerData, map, popup) => {
    // Clean up old event listeners
    marker.off('click popupopen popupclose');

    // Create fresh popup content with new IDs
    const { content, formId, deleteId, submitId } = createPopupContent(markerData, false);

    // Update popup content if provided, otherwise create new popup
    if (popup) {
      popup.setContent(content);
    } else {
      popup = L.popup({
        className: 'custom-popup',
        closeButton: true,
        autoClose: false,
        closeOnClick: false
      }).setContent(content);
      marker.bindPopup(popup);
    }

    // Add click handler
    marker.on('click', (e) => {
      // Stop the event from bubbling up to the map
      L.DomEvent.stopPropagation(e);

      // Close other popups first
      map.eachLayer((layer) => {
        if (layer instanceof L.Marker && layer !== marker) {
          layer.closePopup();
        }
      });

      // Open this marker's popup
      marker.openPopup();

      // Bind event handlers after popup is opened
      const form = document.getElementById(formId);
      const deleteBtn = document.getElementById(deleteId);
      const submitBtn = document.getElementById(submitId);

      if (deleteBtn) {
        deleteBtn.onclick = async () => {
          try {
            deleteBtn.disabled = true;

            console.log('Deleting marker:', markerData.id);
            const response = await fetch(`http://localhost:3001/api/maps/${mapId}/markers/${markerData.id}?campaign_id=${selectedCampaign.id}`, {
              method: 'DELETE',
            });

            if (!response.ok) {
              const errorText = await response.text();
              throw new Error(`Failed to delete marker: ${errorText}`);
            }

            await new Promise(resolve => {
              setMarkers(prev => {
                const updated = prev.filter(m => m.id !== markerData.id);
                resolve(updated);
                return updated;
              });
            });

            map.removeLayer(marker);
          } catch (error) {
            console.error('Error deleting marker:', error);
            deleteBtn.disabled = false;
            alert('Failed to delete marker: ' + error.message);
          }
        };
      }

      if (submitBtn && form) {
        submitBtn.onclick = async () => {
          try {
            submitBtn.disabled = true;

            const formData = new FormData(form);
            const description = formData.get('description');
            const updatedMarker = {
              ...markerData,
              label: formData.get('label'),
              description: description || '',
              color: formData.get('color'),
              campaign_id: selectedCampaign.id,
              lat: Number(markerData.lat),
              lng: Number(markerData.lng),
              map_id: Number(mapId)
            };

            console.log('Updating marker:', markerData.id, updatedMarker);
            const response = await fetch(`http://localhost:3001/api/maps/${mapId}/markers/${markerData.id}`, {
              method: 'PUT',
              headers: { 'Content-Type': 'application/json' },
              body: JSON.stringify(updatedMarker)
            });

            if (!response.ok) {
              const errorText = await response.text();
              throw new Error(`Failed to update marker: ${errorText}`);
            }

            const savedMarker = await response.json();

            const parsedMarker = {
              ...savedMarker,
              id: savedMarker.id,
              lat: Number(savedMarker.lat),
              lng: Number(savedMarker.lng),
              description: savedMarker.description || ''
            };

            await new Promise(resolve => {
              setMarkers(prev => {
                const updated = prev.map(m => m.id === parsedMarker.id ? parsedMarker : m);
                resolve(updated);
                return updated;
              });
            });

            marker.closePopup();
            marker.setIcon(createMarkerIcon(parsedMarker.color));

            // Rebind events with updated data
            bindMarkerEvents(marker, parsedMarker, map, popup);
          } catch (error) {
            console.error('Error updating marker:', error);
            submitBtn.disabled = false;
            alert('Failed to update marker: ' + error.message);
          }
        };
      }
    });

    // Clean up event listeners when popup closes
    marker.on('popupclose', () => {
      const form = document.getElementById(formId);
      const deleteBtn = document.getElementById(deleteId);
      const submitBtn = document.getElementById(submitId);

      if (deleteBtn) deleteBtn.onclick = null;
      if (submitBtn) submitBtn.onclick = null;
      if (form) form.onsubmit = null;
    });

    return popup;
  };

  const addMarkersToMap = (markersData, targetMap) => {
    if (!targetMap) return;

    // Only clear temporary markers
    targetMap.eachLayer((layer) => {
      if (layer instanceof L.Marker && !layer.options.permanent) {
        targetMap.removeLayer(layer);
      }
    });

    // Add new markers
    markersData.forEach(markerData => {
      if (!markerData || typeof markerData.lat !== 'number' || typeof markerData.lng !== 'number') {
        console.warn('Invalid marker data:', markerData);
        return;
      }

      // Create new marker
      const marker = L.marker([markerData.lat, markerData.lng], {
        icon: createMarkerIcon(markerData.color || '#FF0000'),
        permanent: true
      });

      // Bind all events
      bindMarkerEvents(marker, markerData, targetMap);

      marker.addTo(targetMap);
    });
  };

  // Initialize map when data is ready
  useEffect(() => {
    if (!mapData || !imageDimensions || !mapContainerRef.current) return;

    try {
      console.log('Initializing map with dimensions:', imageDimensions);

      // Clean up any existing map first
      cleanupMap();

      const bounds = [[0, 0], [imageDimensions.height, imageDimensions.width]];
      const map = L.map(mapContainerRef.current, {
        crs: L.CRS.Simple,
        maxZoom: 2,
        minZoom: -2,
        maxBounds: bounds,
        zoomControl: true
      });

      // Store the map instance in the ref
      leafletMapRef.current = map;

      const imageOverlay = L.imageOverlay(`http://localhost:3001/maps/${mapData.path}`, bounds);
      imageOverlay.addTo(map);
      map.fitBounds(bounds);

      // Handle ESC key to close popups
      const handleEscKey = (e) => {
        if (e.key === 'Escape') {
          map.eachLayer((layer) => {
            if (layer instanceof L.Marker) {
              layer.closePopup();
            }
          });
        }
      };

      // Add ESC key listener
      document.addEventListener('keydown', handleEscKey);

      // Wait for the image to load before setting up markers
      imageOverlay.once('load', () => {
        console.log('Image overlay loaded');

        // Add click handler for new markers
        map.on('click', (e) => {
          // Only handle map clicks if we're not clicking a marker or popup
          const isMarkerClick = e.originalEvent.target.closest('.leaflet-marker-icon');
          const isPopupClick = e.originalEvent.target.closest('.leaflet-popup');
          const isMapClick = e.originalEvent.target === map.getContainer() || 
                            e.originalEvent.target.classList.contains('leaflet-tile');

          // If clicking on a marker or popup, let the marker's own click handler handle it
          if (isMarkerClick || isPopupClick) {
            return;
          }

          // If clicking on the map, close popups and potentially create a new marker
          map.eachLayer((layer) => {
            if (layer instanceof L.Marker) {
              layer.closePopup();
            }
          });

          if (isMapClick) {
            handleMapClick(e, map);
          }
        });

        // Add existing markers if any
        if (markers.length > 0) {
          console.log('Adding existing markers:', markers.length);
          addMarkersToMap(markers, map);
        }

        setIsLoading(false);
      });

      // Cleanup function
      return () => {
        document.removeEventListener('keydown', handleEscKey);
        cleanupMap();
      };

    } catch (error) {
      console.error('Error initializing map:', error);
      setIsLoading(false);
    }
  }, [mapData, imageDimensions]);

  // Modify handleMapClick to close any open popups before creating a new one
  const handleMapClick = (e, map) => {
    if (!selectedCampaign || !mapId || !map) return;

    // Close any open popups first
    map.eachLayer((layer) => {
      if (layer instanceof L.Marker) {
        layer.closePopup();
      }
    });

    const { lat, lng } = e.latlng;

    // Remove any existing temporary markers
    map.eachLayer((layer) => {
      if (layer instanceof L.Marker && !layer.options.permanent) {
        map.removeLayer(layer);
      }
    });

    const tempMarker = L.marker([lat, lng], {
      icon: createMarkerIcon('#FF0000'),
      permanent: false
    });

    const tempMarkerData = {
      map_id: Number(mapId),
      lat: Number(lat),
      lng: Number(lng),
      label: '',
      description: '',
      color: '#FF0000',
      campaign_id: selectedCampaign.id
    };

    // Create popup with unique IDs
    const { content, formId, deleteId, submitId } = createPopupContent(tempMarkerData, true);
    const popup = L.popup({
      className: 'custom-popup',
      closeButton: true,
      autoClose: false,
      closeOnClick: false
    }).setContent(content);

    tempMarker.bindPopup(popup);

    // Add event listeners when popup opens
    tempMarker.on('popupopen', () => {
      const form = document.getElementById(formId);
      const cancelBtn = document.getElementById(deleteId);
      const createBtn = document.getElementById(submitId);

      if (cancelBtn) {
        cancelBtn.onclick = () => {
          map.removeLayer(tempMarker);
        };
      }

      if (createBtn && form) {
        createBtn.onclick = async () => {
          try {
            createBtn.disabled = true; // Prevent double clicks

            const formData = new FormData(form);
            const newMarker = {
              ...tempMarkerData,
              label: formData.get('label'),
              description: formData.get('description'),
              color: formData.get('color')
            };

            const response = await fetch(`http://localhost:3001/api/maps/${mapId}/markers`, {
              method: 'POST',
              headers: { 'Content-Type': 'application/json' },
              body: JSON.stringify(newMarker)
            });

            if (!response.ok) throw new Error('Failed to create marker');
            const savedMarker = await response.json();

            // Convert lat/lng to numbers and ensure all fields are present
            const parsedMarker = {
              ...savedMarker,
              id: savedMarker.id, // Ensure ID is explicitly set
              lat: Number(savedMarker.lat),
              lng: Number(savedMarker.lng),
              description: savedMarker.description || '',
              campaign_id: selectedCampaign.id,
              map_id: Number(mapId)
            };

            setMarkers(prev => [...prev, parsedMarker]);
            map.removeLayer(tempMarker);

            // Add the new marker
            const marker = L.marker([parsedMarker.lat, parsedMarker.lng], {
              icon: createMarkerIcon(parsedMarker.color || '#FF0000'),
              permanent: true,
              markerId: parsedMarker.id // Set the marker ID in options
            });

            // Bind all events
            bindMarkerEvents(marker, parsedMarker, map);

            marker.addTo(map);
          } catch (error) {
            console.error('Error creating marker:', error);
            createBtn.disabled = false;
            alert('Failed to create marker: ' + error.message);
          }
        };
      }
    });

    // Add event listener for popup close
    tempMarker.on('popupclose', () => {
      // Clean up the temporary marker if it's still on the map
      if (map.hasLayer(tempMarker)) {
        map.removeLayer(tempMarker);
      }
    });

    tempMarker.addTo(map).openPopup();
  };

  const handleMarkerCreate = () => {};
  const handleMarkerUpdate = () => {};
  const handleMarkerDelete = () => {};
  const handleMarkerEdit = () => {};

  if (isLoading || !mapData || !imageDimensions) {
    return (
      <div className="map-container">
        <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'center', height: '100%' }}>
          Loading map...
        </div>
      </div>
    );
  }

  return (
    <div className="map-container">
      <div style={{ display: 'flex', alignItems: 'center', padding: '0 16px' }}>
        <h2 style={{ margin: 0, display: 'flex', alignItems: 'center' }}>{mapData.name}</h2>
        <IconButton
          aria-label="more"
          aria-controls="map-menu"
          aria-haspopup="true"
          onClick={(e) => setMenuAnchorEl(e.currentTarget)}
          style={{ marginLeft: '8px', color: '#111827' }}
        >
          <DotsThreeVertical size={24} />
        </IconButton>
      </div>

      <Menu
        id="map-menu"
        anchorEl={menuAnchorEl}
        keepMounted
        open={Boolean(menuAnchorEl)}
        onClose={() => setMenuAnchorEl(null)}
      >
        <MenuItem onClick={() => setMenuAnchorEl(null)}>Rename</MenuItem>
        <MenuItem onClick={() => setMenuAnchorEl(null)}>Delete</MenuItem>
      </Menu>

      <div ref={mapContainerRef} style={{ height: 'calc(100vh - 80px)', width: '100%' }} />
    </div>
  );
};

export default Maps;
