import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';
import './Maps.css';
import { useCampaign } from '../contexts/CampaignContext';
import IconButton from '@mui/material/IconButton';
import Menu from '@mui/material/Menu';
import MenuItem from '@mui/material/MenuItem';
import { DotsThreeVertical } from "@phosphor-icons/react";

// Marker configuration
const MARKER_COLORS = {
  '#FF0000': 'Red',
  '#00FF00': 'Green',
  '#0000FF': 'Blue',
  '#FFFF00': 'Yellow',
  '#FF00FF': 'Magenta',
  '#00FFFF': 'Cyan',
  '#000000': 'Black'
};

const Maps = ({ onMapChange }) => {
  const { id: mapId } = useParams();
  const navigate = useNavigate();
  const [map, setMap] = useState(null);
  const [leafletMap, setLeafletMap] = useState(null);
  const [mapData, setMapData] = useState(null);
  const [markers, setMarkers] = useState([]);
  const [menuAnchorEl, setMenuAnchorEl] = useState(null);
  const [imageDimensions, setImageDimensions] = useState(null);
  const { selectedCampaign } = useCampaign();
  const mapContainerRef = React.useRef(null);

  // Fetch map data
  useEffect(() => {
    if (selectedCampaign && mapId) {
      fetchMap();
      fetchMarkers();
    }
  }, [mapId, selectedCampaign]);

  const fetchMap = async () => {
    if (!selectedCampaign) return;

    try {
      const response = await fetch(`http://localhost:3001/api/maps/${mapId}?campaign_id=${selectedCampaign.id}`);
      if (!response.ok) throw new Error('Failed to fetch map');
      const data = await response.json();
      setMapData(data);

      // Load image dimensions
      const img = new Image();
      img.onload = () => {
        setImageDimensions({ width: img.width, height: img.height });
        initializeMap(img.width, img.height, data);
      };
      img.src = `http://localhost:3001/maps/${data.path}`;

      if (onMapChange) onMapChange(data);
    } catch (error) {
      console.error('Error fetching map:', error);
      navigate('/maps');
    }
  };

  const fetchMarkers = async () => {
    if (!selectedCampaign || !mapId) return;

    try {
      const response = await fetch(`http://localhost:3001/api/maps/${mapId}/markers?campaign_id=${selectedCampaign.id}`);
      if (!response.ok) throw new Error('Failed to fetch markers');
      const data = await response.json();
      setMarkers(data);

      // If map is already initialized, add markers
      if (leafletMap) {
        addMarkersToMap(data, leafletMap);
      }
    } catch (error) {
      console.error('Error fetching markers:', error);
    }
  };

  const initializeMap = (width, height, mapData) => {
    if (!mapContainerRef.current) return;

    // Clean up existing map
    if (leafletMap) {
      leafletMap.remove();
    }

    const bounds = [[0, 0], [height, width]];
    const map = L.map(mapContainerRef.current, {
      crs: L.CRS.Simple,
      maxZoom: 2,
      minZoom: -2,
      maxBounds: bounds,
      zoomControl: true
    });

    L.imageOverlay(`http://localhost:3001/maps/${mapData.path}`, bounds).addTo(map);
    map.fitBounds(bounds);

    // Add click handler for new markers
    map.on('click', handleMapClick);

    setLeafletMap(map);
    addMarkersToMap(markers, map);
  };

  const createMarkerIcon = (color = '#FF0000') => {
    const svgTemplate = `
      <svg xmlns="http://www.w3.org/2000/svg" width="32" height="48" fill="${color}" viewBox="0 0 256 256">
        <path d="M128,16a88.1,88.1,0,0,0-88,88c0,75.3,80,132.17,83.41,134.55a8,8,0,0,0,9.18,0C136,236.17,216,179.3,216,104A88.1,88.1,0,0,0,128,16Z" stroke="rgba(0, 0, 0, 0.25)" stroke-width="16"/>
      </svg>
    `;
    const svg = `data:image/svg+xml;base64,${btoa(svgTemplate)}`;

    return L.icon({
      iconUrl: svg,
      iconSize: [32, 48],
      iconAnchor: [16, 48],
      popupAnchor: [0, -24]
    });
  };

  const addMarkersToMap = (markersData, targetMap) => {
    if (!targetMap) return;

    // Clear existing markers
    targetMap.eachLayer((layer) => {
      if (layer instanceof L.Marker) {
        targetMap.removeLayer(layer);
      }
    });

    // Add new markers
    markersData.forEach(markerData => {
      const marker = L.marker([Number(markerData.lat), Number(markerData.lng)], {
        icon: createMarkerIcon(markerData.color || '#FF0000')
      });

      const popupContent = createPopupContent(markerData);
      marker.bindPopup(popupContent);
      marker.addTo(targetMap);
    });
  };

  const createPopupContent = (markerData, isNewMarker = false) => {
    const container = document.createElement('div');
    container.className = 'marker-content';

    const header = document.createElement('div');
    header.className = 'marker-header';

    // Create form elements
    const form = document.createElement('form');
    form.onsubmit = (e) => {
      e.preventDefault();
      e.stopPropagation();

      const formData = new FormData(form);
      const updatedMarker = {
        ...markerData,
        label: formData.get('label'),
        description: formData.get('description'),
        color: formData.get('color')
      };

      if (isNewMarker) {
        handleMarkerCreate(updatedMarker);
      } else {
        handleMarkerUpdate(updatedMarker);
      }
    };

    // Label input
    const labelInput = document.createElement('input');
    labelInput.type = 'text';
    labelInput.name = 'label';
    labelInput.value = markerData.label || '';
    labelInput.placeholder = 'Marker Label';
    labelInput.className = 'marker-input';
    labelInput.required = true;

    // Description input
    const descInput = document.createElement('textarea');
    descInput.name = 'description';
    descInput.value = markerData.description || '';
    descInput.placeholder = 'Description (optional)';
    descInput.className = 'marker-input';
    descInput.rows = 2;

    // Color select
    const colorSelect = document.createElement('select');
    colorSelect.name = 'color';
    colorSelect.className = 'marker-input';
    Object.entries(MARKER_COLORS).forEach(([value, label]) => {
      const option = document.createElement('option');
      option.value = value;
      option.textContent = label;
      if (value === (markerData.color || '#FF0000')) {
        option.selected = true;
      }
      colorSelect.appendChild(option);
    });

    // Add inputs to form
    form.appendChild(labelInput);
    form.appendChild(descInput);
    form.appendChild(colorSelect);

    // Create action buttons
    const actions = document.createElement('div');
    actions.className = 'marker-actions';

    const cancelButton = document.createElement('button');
    cancelButton.type = 'button';
    cancelButton.className = 'delete-button';
    cancelButton.textContent = isNewMarker ? 'Cancel' : 'Delete';
    cancelButton.onclick = (e) => {
      e.stopPropagation();
      if (isNewMarker) {
        leafletMap.closePopup();
      } else {
        handleMarkerDelete(markerData.id);
      }
    };

    const submitButton = document.createElement('button');
    submitButton.type = 'submit';
    submitButton.className = 'dnd-button';
    submitButton.textContent = isNewMarker ? 'Add Marker' : 'Save Changes';

    actions.appendChild(cancelButton);
    actions.appendChild(submitButton);
    form.appendChild(actions);

    header.appendChild(form);
    container.appendChild(header);

    return container;
  };

  const handleMapClick = async (e) => {
    if (!selectedCampaign || !mapId) return;

    const { lat, lng } = e.latlng;
    const tempMarker = L.marker([lat, lng], {
      icon: createMarkerIcon('#FF0000')
    });

    const tempMarkerData = {
      lat,
      lng,
      label: '',
      description: '',
      color: '#FF0000'
    };

    const popupContent = createPopupContent(tempMarkerData, true);
    tempMarker.bindPopup(popupContent).addTo(leafletMap).openPopup();
  };

  const handleMarkerDelete = async (markerId) => {
    if (!selectedCampaign || !mapId) return;

    try {
      const response = await fetch(`http://localhost:3001/api/maps/${mapId}/markers/${markerId}?campaign_id=${selectedCampaign.id}`, {
        method: 'DELETE',
      });

      if (!response.ok) throw new Error('Failed to delete marker');

      const updatedMarkers = markers.filter(m => m.id !== markerId);
      setMarkers(updatedMarkers);

      if (leafletMap) {
        addMarkersToMap(updatedMarkers, leafletMap);
      }
    } catch (error) {
      console.error('Error deleting marker:', error);
    }
  };

  const handleMarkerEdit = (marker) => {
    // TODO: Implement edit functionality
    console.log('Edit marker:', marker);
  };

  const handleMarkerCreate = async (markerData) => {
    try {
      const response = await fetch(`http://localhost:3001/api/maps/${mapId}/markers`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          ...markerData,
          campaign_id: selectedCampaign.id
        }),
      });

      if (!response.ok) throw new Error('Failed to create marker');

      const newMarker = await response.json();
      setMarkers(prev => [...prev, newMarker]);

      if (leafletMap) {
        addMarkersToMap([...markers, newMarker], leafletMap);
      }
    } catch (error) {
      console.error('Error creating marker:', error);
    }
  };

  const handleMarkerUpdate = async (updatedMarker) => {
    try {
      const response = await fetch(`http://localhost:3001/api/maps/${mapId}/markers/${updatedMarker.id}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          ...updatedMarker,
          campaign_id: selectedCampaign.id
        }),
      });

      if (!response.ok) throw new Error('Failed to update marker');

      const savedMarker = await response.json();
      setMarkers(prev => prev.map(m => m.id === savedMarker.id ? savedMarker : m));

      if (leafletMap) {
        addMarkersToMap(markers.map(m => m.id === savedMarker.id ? savedMarker : m), leafletMap);
      }
    } catch (error) {
      console.error('Error updating marker:', error);
    }
  };

  if (!mapData || !imageDimensions) {
    return <div>Loading map...</div>;
  }

  return (
    <div className="map-container">
      <div style={{ display: 'flex', alignItems: 'center', padding: '0 16px' }}>
        <h2 style={{ margin: 0, display: 'flex', alignItems: 'center' }}>{mapData.name}</h2>
        <IconButton
          aria-label="more"
          aria-controls="map-menu"
          aria-haspopup="true"
          onClick={(e) => setMenuAnchorEl(e.currentTarget)}
          style={{ marginLeft: '8px', color: '#111827' }}
        >
          <DotsThreeVertical size={24} />
        </IconButton>
      </div>

      <Menu
        id="map-menu"
        anchorEl={menuAnchorEl}
        keepMounted
        open={Boolean(menuAnchorEl)}
        onClose={() => setMenuAnchorEl(null)}
      >
        <MenuItem onClick={() => setMenuAnchorEl(null)}>Rename</MenuItem>
        <MenuItem onClick={() => setMenuAnchorEl(null)}>Delete</MenuItem>
      </Menu>

      <div ref={mapContainerRef} style={{ height: 'calc(100vh - 80px)', width: '100%' }} />
    </div>
  );
};

export default Maps;
